import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CustomerModel } from 'src/app/models/customers-model';
import { TasksModel } from 'src/app/models/tasks-model';
import { DataService } from 'src/app/services/data.service';

@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.css']
})
export class AddComponent implements OnInit{
  public customer: CustomerModel[]
  public newTask = new TasksModel()

  public constructor(private taskService: DataService, private router:Router){}
  async ngOnInit(): Promise<void> {
    try {
      this.customer = await this.taskService.getAllCustomers()      

    } 
    catch (err) {
      console.log (err)      
    }    
  }

  public async send(): Promise<void>{
    try {
      await this.taskService.addTask(this.newTask)
      alert ("The task was successfully added")
      this.router.navigateByUrl("/list")

    } catch (err) {
      console.log (err)
    }
  }
}




<div>


    <form (ngSubmit)="send()" ngNativeValidate>
        <h3>Add a task</h3>
        <select [(ngModel)]="newTask.customerId" name="customerId">


            <option *ngFor="let c of customer" value={{c._id}}>{{c.name}}</option>
        </select>

        <label>NAME</label>
        <input type="text" [(ngModel)]="newTask.name" name="name" placeholder="name" required/>

        <label>DESCRIPTION</label>
        <input type="text" [(ngModel)]="newTask.description" name="description" placeholder="description" required/>

        <label>DATE</label>
        <input type="date" [(ngModel)]="newTask.date" name="date" placeholder="date" required/>

        <label>DONE</label>
        <input type="text" [(ngModel)]="newTask.Done" name="done" placeholder="done">




        <button>ADD</button>

    </form>
